package HW5_BurrowsWheeler;

import edu.princeton.cs.algs4.StdRandom;

/* this implementation uses quick 3-way string sort and 
 * has a severe drawback for a repetitive string*/
public class CircularSuffixArrayV1 {
    private final String s;    
    private final int CUTOFF =  15;   // cutoff to insertion sort
    private CircularSuffix[] array;
    /**
     * sort N circular suffixes in linear space
     * and O(NlgN) time
     * @param s
     */
    public CircularSuffixArrayV1(String s) {
        // circular suffix array of s
        if (s == null) {
            throw new java.lang.NullPointerException();
        }
        // use indices to denote suffixes beginning at
        // particular locations, use radix sort
        // to speed up sorting
        this.s = s;
        this.array = new CircularSuffix[s.length()];
        for (int i = 0; i < s.length(); i++) {
            this.array[i] = new CircularSuffix(s, i);
        }
        //in typical English text, performance is
        //O(nlgn), assuming no long duplicate prefix
        sort(this.array);
    }
    public int length() {
        // length of s
        return s.length();
    }
    public int index(int i) {
        // returns index of ith sorted suffix
        if (i < 0 || i >= length()) {
            throw new java.lang.IndexOutOfBoundsException();
        }
        return this.array[i].index();
    }
    /****************************************************************/
    //quick 3 way string sort (copied from http://algs4.cs.princeton.edu/51radix/Quick3string.java.html)
    private class CircularSuffix {
        private String s;
        private int start;
        /**
         * take a string, and start index of circular suffix
         * @param s
         * @param i
         */
        public CircularSuffix(String s, int i) {
            this.s = s;
            this.start = i;
        }
        //pretend as a string
        public char charAt(int i) {
            return s.charAt((i + start) % s.length());
        }
        //pretend as a string
        public int length() {
            return s.length();
        }
        //return original start index
        public int index() {
            return start;
        }
    }
    /**  
     * Rearranges the array of strings in ascending order.
     *
     * @param a the array to be sorted
     */
    private void sort(CircularSuffix[] a) {
        StdRandom.shuffle(a);
        sort(a, 0, a.length-1, 0);
    }

    // return the dth character of s, -1 if d = length of s
    private int charAt(CircularSuffix s, int d) { 
        assert d >= 0 && d <= s.length();
        if (d == s.length()) return -1;
        return s.charAt(d);
    }

    // 3-way string quicksort a[lo..hi] starting at dth character
    private void sort(CircularSuffix[] a, int lo, int hi, int d) { 

        // cutoff to insertion sort for small subarrays
        if (hi <= lo + CUTOFF) {
            insertion(a, lo, hi, d);
            return;
        }

        int lt = lo, gt = hi;
        int v = charAt(a[lo], d);
        int i = lo + 1;
        while (i <= gt) {
            int t = charAt(a[i], d);
            if      (t < v) exch(a, lt++, i++);
            else if (t > v) exch(a, i, gt--);
            else              i++;
        }
        // a[lo..lt-1] < v = a[lt..gt] < a[gt+1..hi]. 
        sort(a, lo, lt-1, d);
        if (v >= 0) sort(a, lt, gt, d+1);
        sort(a, gt+1, hi, d);
    }

    // sort from a[lo] to a[hi], starting at the dth character
    private void insertion(CircularSuffix[] a, int lo, int hi, int d) {
        for (int i = lo; i <= hi; i++)
            for (int j = i; j > lo && less(a[j], a[j-1], d); j--)
                exch(a, j, j-1);
    }

    // exchange a[i] and a[j]
    private void exch(CircularSuffix[] a, int i, int j) {
        CircularSuffix temp = a[i];
        a[i] = a[j];
        a[j] = temp;
    }

    // is v less than w, starting at character d
    private boolean less(CircularSuffix v, CircularSuffix w, int d) {        
        for (int i = d; i < Math.min(v.length(), w.length()); i++) {
            if (v.charAt(i) < w.charAt(i)) return true;
            if (v.charAt(i) > w.charAt(i)) return false;
        }
        return v.length() < w.length();
    }
    /****************************************************************/

    public static void main(String[] args) {
        // unit testing of the methods (optional)
        //CircularSuffixArray test = new CircularSuffixArray("ABRACADABRA!");
        //check some of the sorted indices
        //System.out.println(test.index(0) == 11 && test.index(1) == 10 && test.index(11) == 2);
        CircularSuffixArrayV1 test2 = new CircularSuffixArrayV1("");
        System.out.println(test2.index(0));
        //* may have overflow!!
    }
}

